import React, { Component, setState } from 'react'
import './Storie.css'
import ReactDOM from 'react-dom'
import Profile from './Profile'


const randomizer = parseInt(Math.random()*72+1);
const imageUrl = require('./imgs/'+randomizer+'.jpg')

export default class App extends Component {
	
  	
  	constructor()
  	{
  		super();
  		this.state={
  			show:false
  		}
  	}
		

	render(){	
	return (

		<div className="overlay">
			<div className="first" style={{backgroundImage:`url(${imageUrl})`}}>
				<div className="progress">
					<div className="bar"></div>

					<button className="close" onClick={() => this.setState({show:!this.state.show})}>X</button>
				</div>
				<div className="loader"></div>
			</div>
		</div>
)
	}
}
const newStorie = parseInt(Math.random()*15000) + 3000;

export function fecharStorie(){
				ReactDOM.unmountMe();
	
}
