import React from 'react'
import $ from 'jquery'
import './Storie.css'

export default props => {
	function fechar(){
		$('.first').css('transform','scale(0) translate(-50%,-50%)');}
	
	var randomizer = parseInt(Math.random()*72+1);
	var url = 'url('+randomizer+'.jpg) #888'

	return(
	
	<div className="first" style={{transform:'scale(0)', background:url}}>
		<div className="progress">
			<div className="bar"></div>

			<button className="close" onClick={fechar}>{props.button}</button>
		</div>
		<div className="loader"></div>
	</div>
		)
}
