import $ from 'jquery'
import './Storie.css'

export default props => {
	function fechar(){
		bgImage()
		$('.first').css('transform','scale(0) translate(-50%,-50%)');}
	
	
	

	function bgImage(props) {
  		const imageUrl = require(`./imgs/${props.imagem}.jpg`)
	
	return <div className="first" style={{backgroundImage:`url(${imageUrl})`}}></div>
	}
}
