import React from 'react'
import './Storie.css'
import ReactDOM from 'react-dom'
import App from '../App';
import Profile from './Profile'


const randomizer = parseInt(Math.random()*72+1);

export default ({ toggle, hidden }) =>{

		state = { hidden: false };
  		toggle = _ => this.setState({ hidden: !this.state.hidden });

  		const imageUrl = require('./imgs/'+randomizer+'.jpg')
  	
	
	return(

		<div className="overlay">
			<div className="first" style={{backgroundImage:`url(${imageUrl})`}}>
				<div className="progress">
					<div className="bar"></div>

					<button className="close" onClick={fecharStorie}>{props.button}</button>
				</div>
				<div className="loader"></div>
			</div>
			{this.state.hidden && <button onClick={this.toggle}>show</button>}
		</div>
)
}


const newStorie = parseInt(Math.random()*15000) + 3000;

export function fecharStorie(){
				ReactDOM.unmountMe();
	
}
