import React from 'react'
import $ from 'jquery'
import './Storie.css'

export default props => {
	function fechar(){
		bgImage()
		$('.first').css('transform','scale(0) translate(-50%,-50%)');}
	
	
	

	function bgImage(props) {
  		const imageUrl = require(`./imgs/${props.imagem}.jpg`)
	
	return <div className="first" style={{transform:'scale(0)', backgroundImage:`url(${imageUrl})`}}>
		<div className="progress">
			<div className="bar"></div>

			<button className="close" onClick={fechar}>{props.button}</button>
		</div>
		<div className="loader"></div>
	</div>
	}
}
