import React from 'react'
import $ from 'jquery'
import './Storie.css'


var randomizer = parseInt(Math.random()*72+1);

	function fechar(){
  		$('.first').css('transform','scale(0)')}

export default props => {

  		const imageUrl = setTimeout(function(){
  			require('./imgs/'+randomizer+'.jpg')
  		},300)
	
	return(
	
	<div className="first" style={{transform:'scale(0)', backgroundImage:`url(${imageUrl})`}}>
		<div className="progress">
			<div className="bar"></div>

			<button className="close" onClick={fechar}>{props.button}</button>
		</div>
		<div className="loader"></div>
	</div>
)
}
