import React from 'react'
import $ from 'jquery'
import './Storie.css'

export default props => {
	function fechar(){
		$('.first').css('transform','scale(0) translate(-50%,-50%)');}
	
	var randomizer = parseInt(Math.random()*72+1);
	

	function bgImage() {
  	const imageUrl = require(`./imgs/${randomizer}.jpg`)
	}


	return(
	
	<div className="first" style={{transform:'scale(0)', backgroundImage:bgImage}}>
		<div className="progress">
			<div className="bar"></div>

			<button className="close" onClick={fechar}>{props.button}</button>
		</div>
		<div className="loader"></div>
	</div>
		)
}
