import React from 'react'
import $ from 'jquery'
import Storie from './Storie'

export default (props) => {
	
	const handC = {
		cursor:props.cursor || 'pointer'
	}
			function fechar(){
		$('.first').css('transform','scale(0) translate(-50%,-50%)');
	}
	  function clicar(){
  		$('.first').css('transform','scale(1) translate(-50%,-50%)');
  	}

 
	return(
	<div className="Profile">

			<div className="Profilefoto" onClick={clicar} style={handC}></div>

			<div className="ProfileText">
				<h2>{props.nome}</h2>
				<p>{props.text}</p>
				<br/><br/>
				<span>doe bitcoin:</span><br/>
				<span>{props.bitcoin}</span>
			</div>
	</div>
	
	)
}
