import React from 'react'
import './Storie.css'
import ReactDOM from 'react-dom'
import App from '../App';
import { Transition } from 'react-transition-group';
import $ from 'jquery'


const randomizer = parseInt(Math.random()*72+1);
var progress = '100%';

export default props => {

  		const imageUrl = require('./imgs/'+randomizer+'.jpg')
  	
	
	return(

		<div className="overlay">
			<div className="first" style={{backgroundImage:`url(${imageUrl})`}}>
				<div className="progress">
					<div className="bar"></div>

					<button className="close" onClick={fecharStorie}>{props.button}</button>
				</div>
				<div className="loader"></div>
			</div>
		</div>
)
}

export function fecharStorie(){
	  window.location.reload(false);
      ReactDOM.render(<App />,document.getElementById('root'));
    }

export 	function progressBar(){
				$('.loader').fadeOut(3000);
			 setTimeout(function(){
			  document.getElementById('bar').style.width = progress;
			 },20)
			}
