import React from 'react'
import ReactDOM from 'react-dom'
import Stories from './Storie'
import $ from 'jquery'
import App from '../App';



export default (props) => {
	const hand = {cursor:props.cursor || 'pointer'}
	return(
	<div className="Profile">
		
			<div className="Profilefoto" onClick={function(e){loadStorie(); progressBar()}} style={hand}>
				<div className="border"></div>
			</div>

			<div className="ProfileText">
				<h2>{props.nome}</h2>
				<p>{props.text}</p>
				<br/><br/>
				<span>doe bitcoin:</span><br/>
				<span>{props.bitcoin}</span>
			</div>
	</div>
	)
}


const progress = '100%';
const newStorie = parseInt(Math.random()*15000) + 3000;

export function loadStorie(){ReactDOM.render(<App/>,document.getElementById('root'));}   

export function progressBar(){
		setTimeout(function(){
			$('.border').css('filter','grayscale(1)'); 
			$('.loader').fadeOut(1000); 
			$('.bar').css('width',progress)},1 )}
