import React from 'react'
import './Storie.css'
import ReactDOM from 'react-dom'
import App from '../App';
import { Transition } from 'react-transition-group';


const randomizer = parseInt(Math.random()*72+1);


export default props => {

  		const imageUrl = require('./imgs/'+randomizer+'.jpg')
  		const duration = 300;

		const defaultStyle = {
  			transition: `opacity ${duration}ms ease-in-out`,
  			opacity: 0,
			}

		const transitionStyles = {
		  entering: { opacity: 1 },
		  entered:  { opacity: 1 },
		  exiting:  { opacity: 0 },
		  exited:  { opacity: 0 },
		};
	
	return(
		<Transition  
			in={true} timeout={duration}
			 {...state => (
     <div style={{
        ...defaultStyle,
        ...transitionStyles[state]
      }}>
        I'm a fade Transition!
      </div>
    )}
		>

		<div className="overlay">
			<div className="first" style={{backgroundImage:`url(${imageUrl})`}}>
				<div className="progress">
					<div className="bar"></div>

					<button className="close" onClick={fecharStorie}>{props.button}</button>
				</div>
				<div className="loader"></div>
			</div>
		</div>

		</Transition>
)
}

export function fecharStorie(){
	  window.location.reload(false);
      ReactDOM.render(<App />,document.getElementById('root'));
    }
