import React from 'react'
import ReactDOM from 'react-dom'
import Stories from './Storie'
import Header from './Header'
import Profile from './Profile'
import TextComponent from './TextComponents'
import Footer from './Footer'
import $ from 'jquery'
import App from '../App';



export default (props) => {
	const hand = {cursor:props.cursor || 'pointer'}
	return(
	<div className="Profile">
		
			<div className="Profilefoto" onClick={function(e){loadStorie(); progressBar()}} style={hand}>
				<div className="border"></div>
			</div>

			<div className="ProfileText">
				<h2>{props.nome}</h2>
				<p>{props.text}</p>
				<br/><br/>
				<span>doe bitcoin:</span><br/>
				<span>{props.bitcoin}</span>
			</div>
	</div>
	)
}

var StoriesRender = (
    <div className="bg">

      <Stories button="X" />
      <Header/>
      <Profile
        cursor="pointer"
        nome="BRUNO"
        bitcoin="1GsaVB7ypw8zkZz5KKL124GcKEi1ZfCZdD"
        text="As the old world fall behind, my spirit reaches wide..."
      />

      <TextComponent/>
      
      <Footer nome="BRUNO"/>
    </div>
  )
const progress = '100%';
export function loadStorie(){
	$('#border').css('filter','grayscale(1)');
	setTimeout(function(){
		 window.location.reload(false); ReactDOM.render(<App />,document.getElementById('root'))},10000)
		 ReactDOM.render(StoriesRender,document.getElementById('root'))}   

export function progressBar(){
		setTimeout(function(){ $('.loader').fadeOut(1000); $('.bar').css('width',progress);},20 )
		setTimeout(function(){
			  var newStorie = Math.floor(Math.random()*15000)+3000;
			$('.border').css('filter','grayscale(1)');
		},newStorie)
		  }
